mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT b.house_num,
REPLACE(REPLACE(b.house_title,'<','&lt;'),'>','&gt;') house_title,
b.house_hits, b.house_recom, b.house_reg_date, b.mem_num, b.house_thumbnail, b.thumbnail_filename, m.nickname, m.profile, m.profile_filename FROM house_board b JOIN mem_detail m ON b.mem_num = m.mem_num
b.mem_num = #{mem_num}
ORDER BY b.house_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE house_board SET
house_thumbnail = #{house_thumbnail}, thumbnail_filename = #{thumbnail_filename},
house_title = #{house_title}, house_area = #{house_area}, house_type = #{house_type}, house_style = #{house_style}, house_space = #{house_space}, house_content = #{house_content}, house_mod_date = SYSDATE WHERE house_num = #{house_num}
